mapper.insert:

	SELECT nextval(USER_SEQ)


INSERT /* LoginMapper.insertLoginInfoOne 사용자 정보 ID,PASS Insert */
INTO LOGIN_INFO(
	USER_NO
	, USER_ID
	, USER_PASS
	, SALT
	, USER_NAME
	, USER_IP
	, DESCRIP
	, GRADE
	, REG_DT
	, CNG_DT
	)
VALUES(
	#{userNo}
	, #{userId}
	, #{userPass}
	, #{salt}
	, #{userName}
	, #{userIp}
	, #{descrip}
	, #{grade}
	, CURRENT_TIMESTAMP()
	, CURRENT_TIMESTAMP()
	)

mapper.update:
UPDATE LOGIN_INFO /* LoginMapper.updateLoginInfoOne 사용자 정보 Update */
SET    USER_PASS = #{userPass}
	   , SALT = #{salt}
	   , USER_NAME  = #{userName}
       , USER_IP = #{userIp}
       , GRADE = #{grade}
WHERE USER_NO = #{userNo}

mapper.insert:
INSERT /* LoginMapper.insertLoginHist 로그인 내역 Insert */
INTO LOGIN_HIST(
	USER_NO
	, LOGIN_IP
	, LOGIN_BROWSER
	, LOGIN_DEVICE
	, SESSION_ID
	, LOGIN_DT
	)
VALUES(
	#{userNo}
	, #{userIp}
	, #{loginBrowser}
	, #{loginDevice}
	, #{sessionId}
	, CURRENT_TIMESTAMP()
	)
